import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const About = () => {
  return (
    <AboutContainer>
      <AboutContent
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1>Sobre a Pousada Pinheira</h1>
        <p>
          Localizada a menos de 50km de Florianópolis e cercada pela Praia do Sonho, Ponta do Papagaio, Praia da Pinheira e Guarda-do-Embaú, a Pousada Pinheira encontra-se a menos de 300m da Praia de Cima, onde águas calmas e limpas fazem a alegria das crianças e trazem tranquilidade para os pais.
        </p>
        <p>
          Uma pousada familiar rodeada de muito verde e belezas naturais, muito aconchegante com um estilo rústico inconfundível. Contamos com uma estrutura de 4 churrasqueiras no pátio, garagem privativa e estacionamento exclusivo, espaço para eventos, guarda-sol e internet wireless. Convidamos você para passar dias inesquecíveis com sua família, nós garantimos o cenário ideal.
        </p>
      </AboutContent>
    </AboutContainer>
  );
};

const AboutContainer = styled.div`
  background-color: #b15a00;
  color: white;
  padding: 40px 20px;
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const AboutContent = styled(motion.div)`
  max-width: 800px;
  text-align: center;

  h1 {
    font-size: 2.5rem;
    margin-bottom: 20px;
  }

  p {
    font-size: 1.2rem;
    line-height: 1.6;
    margin-bottom: 20px;
  }
`;

export default About;
