import React from 'react';
import styled from 'styled-components';
import { FaFacebook, FaInstagram, FaWhatsapp } from 'react-icons/fa';

const Footer = () => {
  return (
    <FooterContainer>
      <FooterContent>
        <Logo src="http://www.pousadapinheira.com.br/img/site/sub-footer-logo.png" alt="Pousada Pinheira" />
        <FooterLinks>
          <FooterLink href="#home">Início</FooterLink>
          <FooterLink href="#about">Sobre</FooterLink>
          <FooterLink href="#accommodations">Acomodações</FooterLink>
          <FooterLink href="#packages">Pacotes</FooterLink>
          <FooterLink href="#contact">Contato</FooterLink>
        </FooterLinks>
        <SocialMedia>
          <SocialLink href="https://www.facebook.com" target="_blank" rel="noopener noreferrer">
            <FaFacebook />
          </SocialLink>
          <SocialLink href="https://www.instagram.com" target="_blank" rel="noopener noreferrer">
            <FaInstagram />
          </SocialLink>
          <SocialLink href="https://api.whatsapp.com/send?l=pt_br&phone=5548999720998&text=Oi,%20estou%20em%20seu%20site%20e%20gostaria%20de%20algumas%20informa%C3%A7%C3%B5es,%20consegue%20me%20ajudar?" target="_blank" rel="noopener noreferrer">
            <FaWhatsapp />
          </SocialLink>
        </SocialMedia>
      </FooterContent>
      <FooterBottom>
        <Address>Rua das Gaivotas n 283, Praia de cima - Pinheira - Palhoça - SC</Address>
        <CopyRight>© 2024 Pousada Pinheira. Todos os direitos reservados.</CopyRight>
      </FooterBottom>
    </FooterContainer>
  );
};

const FooterContainer = styled.footer`
  background-color: #b15a00;
  color: white;
  padding: 20px 0;
`;

const FooterContent = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 20px;
  flex-wrap: wrap;
`;

const Logo = styled.img`
  height: 50px;
`;

const FooterLinks = styled.div`
  display: flex;
  gap: 20px;
`;

const FooterLink = styled.a`
  color: white;
  text-decoration: none;
  font-size: 1rem;

  &:hover {
    text-decoration: underline;
  }
`;

const SocialMedia = styled.div`
  display: flex;
  gap: 15px;
`;

const SocialLink = styled.a`
  color: white;
  font-size: 1.5rem;

  &:hover {
    color: #ffeb3b;
  }
`;

const FooterBottom = styled.div`
  text-align: center;
  padding: 10px 0;
  background-color: #8c4600;
`;

const Address = styled.p`
  margin: 0;
`;

const CopyRight = styled.p`
  margin: 0;
  font-size: 0.875rem;
`;

export default Footer;
