import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Breakfast = () => {
  return (
    <BreakfastContainer>
      <Header>
        <Title
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          Café da Manhã
        </Title>
      </Header>
      <Content>
        <ImageContainer
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.8 }}
        >
          <img src="/path/to/breakfast_image.jpg" alt="Café da Manhã" />
        </ImageContainer>
        <TextContainer
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <p>
            Comece o seu dia com um delicioso café da manhã na Pousada Pinheira. 
            Oferecemos uma variedade de opções para todos os gostos:
          </p>
          <ul>
            <li>Pães frescos e variados</li>
            <li>Frutas da estação</li>
            <li>Suco natural</li>
            <li>Café, leite e chás</li>
            <li>Iogurtes e cereais</li>
            <li>Bolos e doces caseiros</li>
          </ul>
          <p>
            Venha desfrutar de um café da manhã completo e saboroso com uma vista deslumbrante. 
            Nosso café da manhã é servido diariamente das 7h às 10h.
          </p>
        </TextContainer>
      </Content>
    </BreakfastContainer>
  );
};

const BreakfastContainer = styled.div`
  padding: 50px;
  background-color: #f4f4f4;
  color: #333;
`;

const Header = styled(motion.div)`
  text-align: center;
  margin-bottom: 50px;
`;

const Title = styled(motion.h1)`
  font-size: 2.5rem;
  color: #008080;
`;

const Content = styled.div`
  display: flex;
  flex-wrap: wrap;
  align-items: flex-start;
  justify-content: center;
`;

const ImageContainer = styled(motion.div)`
  flex: 1;
  margin: 20px;
  max-width: 400px;
  img {
    width: 100%;
    height: auto;
    border-radius: 10px;
  }
`;

const TextContainer = styled(motion.div)`
  flex: 2;
  margin: 20px;
  max-width: 600px;
  p {
    font-size: 1.2rem;
    line-height: 1.6;
    margin-bottom: 20px;
  }
  ul {
    list-style-type: disc;
    padding-left: 20px;
    margin-bottom: 20px;
    li {
      font-size: 1.2rem;
      margin-bottom: 10px;
    }
  }
`;

export default Breakfast;
