import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import whatsappIcon from '../assets/whatsapp_icon.png'; // Certifique-se de ter um ícone do WhatsApp na pasta assets





const Home = () => {
  return (
    <HomeContainer>

      <HeroSection>
        <HeroText
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h1>Bem-vindo à Pousada Pinheira</h1>
          <p>O destino perfeito para suas férias inesquecíveis</p>


        </HeroText>
      </HeroSection>

    </HomeContainer>
  );
};


const HomeContainer = styled.div`
  padding: 20px;
  background-color: #b15a00;
  color: #333;
`;

const HeroSection = styled.div`
  background: url('http://www.pousadapinheira.com.br/img/site/container-bg.jpg') center/cover no-repeat;
  height: 60vh;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 50px;
`;

const HeroText = styled(motion.div)`
  text-align: center;
  color: white;
  h1 {
    font-size: 3rem;
    margin-bottom: 20px;
  }
  p {
    font-size: 1.5rem;
  }
`;


const Home2 = () => {
  return (
    <Home2>

      <HeroSection>
        <HeroText
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h1>Bem-vindo à Pousada Pinheira</h1>
          <p>O destino perfeito para suas férias inesquecíveis</p>


        </HeroText>
      </HeroSection>

    </Home2>
  );
};


const Home2Container = styled.div`
  padding: 20px;
  background-color: #b15a00;
  color: #333;
`;









export default Home; Home2;
