import React from 'react';
import styled from 'styled-components';
import pousadaImage from '../assets/pousada.jpg'; // Ajuste o caminho conforme a localização da imagem


const ContactContainer = styled.div`
  padding: 2rem;
  text-align: center;
`;
const Image = styled.img`
  width: 100%; /* Ajuste o tamanho conforme necessário */
  max-width: 600px; /* Limite o tamanho máximo da imagem */
  height: auto;
  border-radius: 8px; /* Adicione bordas arredondadas se desejar */
`;

const Contact = () => (
  <ContactContainer>
    <h2>Contato</h2>
    <p>Entre em contato conosco para mais informações.</p>
    <p>Rua das Gaivotas, 283, Praia de Cima - Pinheira, Palhoça - SC</p>
    <p>
      <a href="https://api.whatsapp.com/send?l=pt_br&phone=5548999720998&text=Oi,%20estou%20em%20seu%20site%20e%20gostaria%20de%20algumas%20informa%C3%A7%C3%B5es,%20consegue%20me%20ajudar?">
        Fale conosco no WhatsApp
      </a>
    </p>
    <iframe
      src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3520.060424663699!2d-48.62977568493248!3d-27.886464282723537!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x95275ac60abbbd37%3A0x4d8b8d589def9e0c!2sR.%20das%20Gaivotas%2C%20283%20-%20Praia%20de%20Cima%20(Pinheira)%2C%20Palho%C3%A7a%20-%20SC%2C%2088139-399%2C%20Brasil!5e0!3m2!1spt-BR!2sus!4v1640894144706!5m2!1spt-BR!2sus"
      width="600"
      height="450"
      allowFullScreen=""
      loading="lazy"
      title="Localização no Google Maps"
    ></iframe>
  </ContactContainer>
);

export default Contact;
