import React from 'react';
import styled from 'styled-components';
import headerImage from '../assets/header_image.jpg'; // Certifique-se de ter uma imagem de cabeçalho
import galleryImage1 from '../assets/gallery1.jpg'; // Adicione imagens à pasta assets
import galleryImage2 from '../assets/gallery2.jpg';
import galleryImage3 from '../assets/gallery3.jpg';
import galleryImage4 from '../assets/gallery4.jpg';
import galleryImage5 from '../assets/gallery5.jpg';

const Home = () => {
  return (
    <HomeContainer>
      <Header>
        <HeaderImage src={headerImage} alt="Pousada Pinheira" />
        <HeaderTitle>Bem-vindo à Pousada Pinheira</HeaderTitle>
      </Header>
      <Gallery>
        <GalleryTitle>Nossa Galeria</GalleryTitle>
        <GalleryImages>
          <Image src={galleryImage1} alt="Galeria 1" />
          <Image src={galleryImage2} alt="Galeria 2" />
          <Image src={galleryImage3} alt="Galeria 3" />
          <Image src={galleryImage4} alt="Galeria 4" />
          <Image src={galleryImage5} alt="Galeria 5" />
        </GalleryImages>
      </Gallery>
    </HomeContainer>
  );
};

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const Header = styled.div`
  position: relative;
  width: 100%;
  height: 400px;
  margin-bottom: 40px;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const HeaderImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  filter: brightness(70%);
`;

const HeaderTitle = styled.h1`
  position: absolute;
  color: white;
  font-size: 3rem;
  text-align: center;
`;

const Gallery = styled.div`
  width: 100%;
  max-width: 1200px;
  text-align: center;
`;

const GalleryTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 20px;
`;

const GalleryImages = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
`;

const Image = styled.img`
  width: 32%;
  height: 200px;
  object-fit: cover;
  margin-bottom: 20px;
  border-radius: 10px;
`;

export default Home;
