import React from 'react';
import { Link } from 'react-router-dom';

import styled from 'styled-components';
import logo from '../assets/logo.png'; // ajuste o caminho conforme a localização da imagem


const HeaderContainer = styled.header`
  background-color: #008B8B;
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const NavLinks = styled.nav`
  a {
    margin: 0 1rem;
    text-decoration: none;
    color: white;
    font-weight: bold;
  }
`;


const Header = () => (
  <HeaderContainer>
    <Link to="/">
      <img src={logo} alt="Pousada Pinheira Logo" />
    </Link>

    <NavLinks>
      <Link to="/">Home</Link>
      <Link to="/about">A Pousada</Link>
      <Link to="/accommodations">Acomodações</Link>
      <Link to="/packages">Pacotes</Link>
      <Link to="/contact">Contato</Link>
      <Link to="/breakfast">Café da Manhã</Link>
    </NavLinks>
  </HeaderContainer>
);

export default Header;
