import React, { useState } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

// Estilos para o menu de navegação
const NavbarContainer = styled.nav`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #333;
  color: white;
  width: 100%;
`;

const NavHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  padding: 10px 20px;
`;

const NavLinks = styled.div`
  display: ${({ isOpen }) => (isOpen ? 'flex' : 'none')};
  flex-direction: column;
  align-items: center;
  width: 100%;
  background-color: #333;

  @media (min-width: 768px) {
    display: flex;
    flex-direction: row;
    justify-content: center;
  }
`;

const NavLink = styled(Link)`
  color: white;
  text-decoration: none;
  padding: 10px 20px;
  width: 100%;
  text-align: center;

  &:hover {
    background-color: #444;
  }

  @media (min-width: 768px) {
    width: auto;
  }
`;

const HamburgerIcon = styled.a`
  display: flex;
  flex-direction: column;
  cursor: pointer;

  @media (min-width: 768px) {
    display: none;
  }
`;

const Bar = styled.div`
  height: 3px;
  width: 25px;
  background-color: white;
  margin: 4px 0;
`;

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <NavbarContainer>
      <NavHeader>
        <h1>Pousada Pinheira</h1>
        <HamburgerIcon
          href="#"
          className="fusion-icon fusion-icon-bars"
          aria-label="Toggle mobile menu"
          aria-expanded={isOpen}
          aria-controls="mobile-menu-principal"
          onClick={toggleMenu}
        >
          <Bar />
          <Bar />
          <Bar />
        </HamburgerIcon>
      </NavHeader>
      <NavLinks isOpen={isOpen} id="mobile-menu-principal">
        <NavLink to="/">Home</NavLink>
        <NavLink to="/about">About</NavLink>
        <NavLink to="/breakfast">Café da Manhã</NavLink>
        <NavLink to="/contact">Contact</NavLink>
      </NavLinks>
    </NavbarContainer>
  );
};

export default Navbar;
