import React from 'react';
import styled from 'styled-components';
import pousadaImage from '../assets/pousada.jpg'; // Ajuste o caminho conforme a localização da imagem


const ContactContainer = styled.div`
  padding: 2rem;
  text-align: center;
`;
const Image = styled.img`
  width: 100%; /* Ajuste o tamanho conforme necessário */
  max-width: 600px; /* Limite o tamanho máximo da imagem */
  height: auto;
  border-radius: 8px; /* Adicione bordas arredondadas se desejar */
`;

const Contact = () => (
  <ContactContainer>
    <h2>Contato</h2>
    <p>Entre em contato conosco para mais informações.</p>
    <Image src={pousadaImage} alt="Pousada Pinheira" />


    <iframe
      src="1640894144706!5m2!1spt-BR!2sus"
      width="400"
      height="300"
      allowFullScreen=""
      loading="lazy"
      title="Localização no Google Maps"
    ></iframe>
    <p>
      <a href="https://api.whatsapp.com/send?l=pt_br&phone=5548999720998&text=Oi,%20estou%20em%20seu%20site%20e%20gostaria%20de%20algumas%20informa%C3%A7%C3%B5es,%20consegue%20me%20ajudar?">
        Fale conosco no WhatsApp
      </a>
    </p>
    <p>Rua das Gaivotas, 283, Praia de Cima - Pinheira, Palhoça - SC</p>
  </ContactContainer>
);

export default Contact;
