import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';


const Home = () => {
  return (
    <HomeContainer>
      <HeroSection>
        <HeroText
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h1>Bem-vindo à Pousada Pinheira</h1>
          <p>O destino perfeito para suas férias inesquecíveis</p>
        </HeroText>
      </HeroSection>
      <ContentSection>
        <Card
          initial={{ opacity: 0, scale: 0.8 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
        >
          <img src="http://www.pousadapinheira.com.br/img/paginas/2/Acomodacoes_8.jpg" alt="Acomodações" />
          <CardContent>
            <h2>Acomodações</h2>
            <p>Descanse em nossas confortáveis e bem equipadas acomodações.</p>
            <Link to="/accommodations">Saiba Mais</Link>
          </CardContent>
        </Card>
        <Card
          initial={{ opacity: 0, scale: 0.8 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
        >
          <img src="http://www.pousadapinheira.com.br/img/paginas/2/Acomodacoes_8.jpg" alt="Café da Manhã" />
          <CardContent>
            <h2>Café da Manhã</h2>
            <p>Aproveite um café da manhã delicioso e completo.</p>
            <Link to="/breakfast">Saiba Mais</Link>
          </CardContent>
        </Card>
        <Card
          initial={{ opacity: 0, scale: 0.8 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
        >
          <img src="http://www.pousadapinheira.com.br/img/paginas/2/Acomodacoes_8.jpg" alt="Pacotes" />
          <CardContent>
            <h2>Pacotes</h2>
            <p>Confira nossos pacotes especiais para feriados e datas comemorativas.</p>
            <Link to="/packages">Saiba Mais</Link>
          </CardContent>
        </Card>
      </ContentSection>
    </HomeContainer>
  );
};
const About = () => {
  return (
    <AboutContainer>
      <Header>
        <Title
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          Sobre a Pousada Pinheira
        </Title>
      </Header>
      <Content>
        <ImageContainer
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.8 }}
        >
          <img src="http://www.pousadapinheira.com.br/img/site/conteudo-img.png" alt="Pousada Pinheira" />
        </ImageContainer>
        <TextContainer
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <p>
            Localizada a menos de 50km de Florianópolis e cercada pela Praia do Sonho,
            Ponta do Papagaio, Praia da Pinheira e Guarda-do-Embaú, a Pousada Pinheira
            encontra-se a menos de 300m da Praia de Cima, onde águas calmas e limpas
            fazem a alegria das crianças e trazem tranquilidade para os pais.
          </p>
          <p>
            Uma pousada familiar rodeada de muito verde e belezas naturais, muito
            aconchegante com um estilo rústico inconfundível. Contamos com uma
            estrutura de 4 churrasqueiras no pátio, garagem privativa e estacionamento
            exclusivo, espaço para eventos, guarda-sol e internet wireless.
          </p>
          <p>
            Convidamos você para passar dias inesquecíveis com sua família, nós
            garantimos o cenário ideal.
          </p>
        </TextContainer>
      </Content>
    </AboutContainer>
  );
};


const AboutContainer = styled.div`
  padding: 50px;
  background-color: #b15a00;
  olor: white;
  padding: 40px 20px;
  min-height: 100vh;

  color: #333;
`;


const Header = styled(motion.div)`
  text-align: center;
  margin-bottom: 50px;
`;

const Title = styled(motion.h1)`
  font-size: 2.5rem;
  color: #ffffff;
`;

const Content = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-around;
  flex-wrap: wrap;
  color: #ffffff;
`;

const ImageContainer = styled(motion.div)`
  flex: 1;
  margin: 20px;
  img {
    width: 100%;
    height: auto;
    border-radius: 15px;
  }
`;

const TextContainer = styled(motion.div)`
  flex: 2;
  margin: 20px;
  p {
    font-size: 1.2rem;
    line-height: 1.6;
    margin-bottom: 20px;
  }
`;

export default About;
