import React from "react";
import styled from "styled-components";


const accommodations = [
  {
    image: "https://ibb.co/KhkGHS8",
    title: "Quarto Standard",
    description:
      "Um quarto confortável e acessível, ideal para estadias curtas.",
  },
  {
    image: "https://via.placeholder.com/600x400",
    title: "Suíte Deluxe",
    description:
      "Uma suíte espaçosa com todas as comodidades para uma estadia luxuosa.",
  },
  {
    image: "https://via.placeholder.com/600x400",
    title: "Quarto Familiar",
    description:
      "Quarto projetado para acomodar toda a família com espaço e conforto.",
  },
  // Adicione mais acomodações conforme necessário
];

const Breakfast = () => (
  <BreakfastContainer>
    <Section>
      <h1>Café da Manhã</h1>
      <p>No Café da Manhã da Pousada Pinheira, oferecemos uma seleção deliciosa e variada para começar bem o seu dia. Nossa oferta inclui:</p>
      <ul>
        <li>Frutas frescas da estação</li>
        <li>Pães e bolos caseiros</li>
        <li>Cafés, chás e sucos naturais</li>
        <li>Opções de queijos e frios</li>
        <li>Pratos quentes como omeletes e tapiocas</li>
      </ul>
      <p>Desfrute de um café da manhã acolhedor em um ambiente tranquilo, ideal para começar o seu dia de forma especial.</p>
    </Section>
    <GalleryContainer>
      <GalleryImage src={breakfast1} alt="Café da Manhã 1" />
      <GalleryImage src={breakfast2} alt="Café da Manhã 2" />
      <GalleryImage src={breakfast3} alt="Café da Manhã 3" />
      <GalleryImage src={breakfast4} alt="Café da Manhã 4" /> {/* Adicione mais imagens conforme necessário */}
    </GalleryContainer>
  </BreakfastContainer>
);

const PageContainer = styled.div`
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const HeroSection = styled.div`
  background: ${(props) => props.theme.colors.secondary};
  color: #fff;
  padding: 2rem;
  text-align: center;
  border-radius: 8px;
  margin-bottom: 2rem;
`;

const AccommodationList = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 1rem;
`;

export default Acomodações;
