import React from 'react';
import styled from 'styled-components';
import AccommodationCard from '../components/Accommodations';

const accommodations = [
  {
    image: 'https://via.placeholder.com/600x400',
    title: 'Quarto Standard',
    description: 'Um quarto confortável e acessível, ideal para estadias curtas.',
  },
  {
    image: 'https://via.placeholder.com/600x400',
    title: 'Suíte Deluxe',
    description: 'Uma suíte espaçosa com todas as comodidades para uma estadia luxuosa.',
  },
  {
    image: 'https://via.placeholder.com/600x400',
    title: 'Quarto Familiar',
    description: 'Quarto projetado para acomodar toda a família com espaço e conforto.',
  },
  // Adicione mais acomodações conforme necessário
];

const Acomodações = () => {
  return (
    <PageContainer>
      <HeroSection>
        <h1>Acomodações</h1>
        <p>Explore nossas opções de acomodação e encontre a perfeita para sua estadia.</p>
      </HeroSection>
      <AccommodationList>
        {accommodations.map((acc, index) => (
          <AccommodationCard
            key={index}
            image={acc.image}
            title={acc.title}
            description={acc.description}
          />
        ))}
      </AccommodationList>
    </PageContainer>
  );
};

const PageContainer = styled.div`
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const HeroSection = styled.div`
  background: ${props => props.theme.colors.secondary};
  color: #fff;
  padding: 2rem;
  text-align: center;
  border-radius: 8px;
  margin-bottom: 2rem;
`;

const AccommodationList = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 1rem;
`;

export default Acomodações;
