import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import finalYearPackage from '../assets/final_year_package.jpg';
import nationalHolidaysPackage from '../assets/national_holidays_package.jpg';
import carnivalPackage from '../assets/carnival_package.jpg';

const PackagesContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  background-color: #E0FFFF; // Verde piscina
`;

const PackagesGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 2rem;
`;

const PackageCard = styled(motion.div)`
  background: white;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  margin: 1rem;
  width: 250px;
  text-align: center;

  img {
    width: 100%;
    height: 150px;
    object-fit: cover;
  }

  h3 {
    margin: 1rem 0;
  }

  p {
    padding: 0 1rem 1rem;
  }
`;

const Packages = () => {
  return (
    <PackagesContainer>
      <h1>Nossos Pacotes</h1>
      <PackagesGrid>
        <PackageCard
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <img src={finalYearPackage} alt="Pacote de Final de Ano" />
          <h3>Pacote de Final de Ano</h3>
          <p>Desfrute de uma virada de ano inesquecível na Pousada Pinheira!</p>
        </PackageCard>
        <PackageCard
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <img src={nationalHolidaysPackage} alt="Pacote de Feriados Nacionais" />
          <h3>Pacote de Feriados Nacionais</h3>
          <p>Relaxe e aproveite os feriados com conforto e diversão garantidos.</p>
        </PackageCard>
        <PackageCard
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <img src={carnivalPackage} alt="Pacote de Carnaval" />
          <h3>Pacote de Carnaval</h3>
          <p>Entre no ritmo do Carnaval com muita alegria e festividade!</p>
        </PackageCard>
      </PackagesGrid>
    </PackagesContainer>
  );
};

export default Packages;
