import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import whatsappIcon from '../assets/whatsapp_icon.png'; // Certifique-se de ter um ícone do WhatsApp na pasta assets

const About = () => {
  return (
    <HomeContainer>
      <HomeContent>
        <Title>Sobre a Pousada Pinheira</Title>
        <Description>
          Localizada a menos de 50km de Florianópolis e cercada pela Praia do Sonho, Ponta do Papagaio, Praia da Pinheira e Guarda-do-Embaú, a Pousada Pinheira encontra-se a menos de 300m da Praia de Cima, onde águas calmas e limpas fazem a alegria das crianças e trazem tranquilidade para os pais.
          <br /><br />
          Uma pousada familiar rodeada de muito verde e belezas naturais, muito aconchegante com um estilo rústico inconfundível.
          <br /><br />
          Contamos com uma estrutura de 4 churrasqueiras no pátio, garagem privativa e estacionamento exclusivo, espaço para eventos, guarda-sol e internet wireless.
          <br /><br />
          Convidamos você para passar dias inesquecíveis com sua família, nós garantimos o cenário ideal.
        </Description>

          Fale Conosco

      </HomeContent>
    </HomeContainer>
  );
};

const Home = () => {
  return (
    <HomeContainer>

      <HeroSection>
        <HeroText
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h1>Bem-vindo à Pousada Pinheira</h1>
          <p>O destino perfeito para suas férias inesquecíveis</p>

          <p>Aproveite um café da manhã delicioso e completo.</p>
          <p> Descanse em nossas confortáveis e bem equipadas acomodações.</p>
          <p>Confira nossos pacotes especiais para feriados e datas comemorativas.</p>


        </HeroText>
      </HeroSection>

    </HomeContainer>
  );
};

const HomeContainer = styled.div`
  padding: 20px;
  background-color: #b15a00;
  color: #333;
`;

const HeroSection = styled.div`
  background: url('http://www.pousadapinheira.com.br/img/site/container-bg.jpg') center/cover no-repeat;
  height: 60vh;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 50px;
`;

const HeroText = styled(motion.div)`
  text-align: center;
  color: white;
  h1 {
    font-size: 3rem;
    margin-bottom: 20px;
  }
  p {
    font-size: 1.5rem;
  }
`;






const WhatsAppButton = styled.a`
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 10px 20px;
  background-color: #25d366;
  color: white;
  text-decoration: none;
  font-size: 1.2rem;
  border-radius: 5px;
  transition: background-color 0.3s;

  img {
    width: 44px;
    height: 44px;
    margin-right: 10px;
  }

  &:hover {
    background-color: #1ebd59;
  }
`;
export default Home;
